import React, { useState, useEffect, Context } from "react";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import Card from "@mui/material/Card";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import { TextFieldComponent } from "../../components/TextFieldComponent";
import { ValidationMessage } from "../../components/ValidationComponent";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import { DataValue } from "./store/types";

import Select, { SelectChangeEvent } from "@mui/material/Select";
import "./style.css";

export const Form = (props: any): JSX.Element => {
  const { formReducer, formData, formRequest } = props;

  const users = formReducer.users;
  console.log(formReducer);

  const [age, setAge] = React.useState("");

  // const data: any = {};

  const handleChange = (
    event: SelectChangeEvent | React.ChangeEvent<HTMLInputElement>
  ) => {
    const { name, value } = event.target;
    // data[name] = value;
    formData({ ...formReducer.formData, [name]: value });
  };

  const submitForm = () => {
    formRequest(formReducer.formData);
  };

  return (
    <Container maxWidth="xl">
      <Box>
        <Grid container spacing={2}>
          <Grid item xs={12} className="mt-5 center">
            {/* <Card variant="outlined" className="center"> */}
            <h2>Add New</h2>
            {formReducer.successMessage !== null && (
              <div className="message_width">
                <ValidationMessage
                  type="success"
                  messages={`Success - ${JSON.stringify(
                    formReducer.successMessage
                  )}`}
                />
              </div>
            )}
            {formReducer.errorMessage !== null && (
              <div className="message_width">
                <ValidationMessage
                  type="Error"
                  messages={`Error - ${JSON.stringify(
                    formReducer.errorMessage
                  )}`}
                />
              </div>
            )}
            <div className="form_section">
              <FormControl className="input_size">
                <InputLabel id="demo-simple-select-label">User Id</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  label="User Id"
                  onChange={handleChange}
                  name="userId"
                  value={formReducer.formData.userId ?? ""}
                  required={true}
                >
                  <MenuItem value="" hidden>
                    Select User ID
                  </MenuItem>
                  {users.map((user: any, index: number) => (
                    <MenuItem value={user.id} key={index}>
                      {user.id}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>

              <TextFieldComponent
                color="primary"
                id="text-title"
                name="title"
                label="Title"
                className="input_size"
                onchangeHandler={handleChange}
                value={formReducer.formData.title ?? ""}
                required={true}
              />

              <TextFieldComponent
                color="primary"
                id="text-body"
                name="body"
                label="Body"
                multiline={true}
                className="input_size"
                value={formReducer.formData.body ?? ""}
                onchangeHandler={handleChange}
                required={true}
              />

              <Button type="submit" variant="contained" onClick={submitForm}>
                Contained
              </Button>
            </div>
            {/* </Card> */}
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};
