import React, { useState, useEffect, Context } from "react";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import Modal from "@mui/material/Modal";
import { DataValue } from "./store/types";
import { TextFieldComponent } from "../../components/TextFieldComponent";
import { ValidationMessage } from "../../components/ValidationComponent";

export const Dashboard = (props: any): JSX.Element => {
  const {
    dashboardRequest,
    dashboardReducer,
    formReducer,
    formData,
    formRequest,
  } = props;
  const { dataRecords } = dashboardReducer;

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  useEffect(() => {
    dashboardRequest();
  }, []);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    formData({ ...formReducer.formData, [name]: value });
  };

  const submitForm = (evt: React.FormEvent<HTMLFormElement>) => {
    evt.preventDefault();
    formRequest(formReducer.formData);
  };

  return (
    <Box sx={{ minWidth: 275, marginTop: 10 }}>
      <Grid container spacing={2}>
        {dataRecords.map((data: DataValue, index: number) => (
          <Grid item xs={12} key={index}>
            <Card variant="outlined">
              <CardContent>
                <Button sx={{ float: "right" }} onClick={handleOpen}>
                  Edit
                </Button>
                <Typography variant="h5" component="div">
                  {data.title}
                </Typography>
                <Typography variant="body2">{data.body}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: 400,
            backgroundColor: "white",
            border: "2px solid #000",
            boxShadow: "1px 2px 9px #F4AAB9",
            padding: 4,
          }}
        >
          <div className="message_width">
            {/* <ValidationMessage
              type="success"
              messages={`Success.. ID-${formReducer.successMessage.id} created`}
            /> */}
          </div>

          <form onSubmit={submitForm} className="form_section">
            <h2>Add New Resource</h2>

            <TextFieldComponent
              color="primary"
              id="text-title"
              name="title"
              label="Title"
              className="input_size"
              onchangeHandler={handleChange}
              value={formReducer.formData.title ?? ""}
              required={true}
            />

            <TextFieldComponent
              color="primary"
              id="text-body"
              name="body"
              label="Body"
              multiline={true}
              className="input_size"
              value={formReducer.formData.body ?? ""}
              onchangeHandler={handleChange}
              required={true}
            />

            <Button type="submit" variant="contained">
              Submit
            </Button>
          </form>
        </Box>
      </Modal>
    </Box>
  );
};
