import { ApiMethods, callApi } from "../../../utils/api";
import { all, call, fork, put, takeEvery } from "typed-redux-saga";
import { dashboardRequest, dashboardSuccess, dashboardFailed } from "./actions";

import { API_ROUTES } from "../../../config/api_routes";
import { FormActionTypes } from "./types";

export function* formHandler(action: ReturnType<typeof dashboardRequest>): any {
  try {
    const formValues = action.payload;
    const response = yield* call(
      callApi,
      ApiMethods.POST.toString(),
      API_ROUTES.GET_DASHBOARD_DATA,
      formValues
    );
    console.log(response);
    // if (response.error) {
    //   yield put(dashboardFailed(response.error));
    // } else {
    //   yield put(dashboardSuccess(response));
    // }
  } catch (e) {
    if (e instanceof Error && e.stack) yield put(dashboardFailed(e.stack));
    else yield put(dashboardFailed("unknown error."));
  }
}

function* watchFormHandler() {
  yield takeEvery(FormActionTypes.FORM_REQUEST, formHandler);
}

function* formSaga(): any {
  yield all([fork(watchFormHandler)]);
}

export default formSaga;
